package Day05;

public class test10 {

	/**
	 * @param args 练习使用三重循环
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<10;i++){
			for(int j=0;j<10;j++){
				for(int k=0;k<10;k++){
				System.out.print(i*100+j*10+k+" ");
			}
				System.out.println();
			}
			System.out.println();
		}

	}

}
